import React,{useState} from 'react'
import { StyleSheet, Text, View,Image, TextInput,TouchableWithoutFeedback,TouchableOpacity } from 'react-native'




const Loginpage = () => {
    const connecter = 'https://madad-22e60-default-rtdb.firebaseio.com/'; 
    const [id,setid] = React.useState('User1');
    const [help, setHelp]=React.useState('');
    const postData = () => {
        var requestOptions = {
          method: 'POST',
          body: JSON.stringify({
            email: 'name',
            pass: 'terapaoo',
        }),
        };
    
        fetch(`${connecter}/userlogins.json`, requestOptions)
          .then((response) => response.json())
          .then((result) => console.log(result))
          .catch((error) => console.log('error', error));
      };
    const handleSignup = async () => {
        
            const response = await fetch(`${connecter}`);
            const data = await response.json();
            setHelp(data.pass)
            
           
          };
        
    const [email,setemail ]= React.useState('');
    const [pass,setpass ]= React.useState('');
    
    return (
        <View style={styles.container}>
        <View>
<Image
  source={require("../assets/login1.png")}
  style={styles.mainimage}
/>   
</View> 
<View style={styles.inputcontainer}> 
<TextInput
placeholder='Enter Your Email'
onChangeText={(input)=>{setemail(input)}}
style={styles.inputfield}
/>
<TextInput
placeholder='Enter Password'
onChangeText={(input)=>{setpass(input)}}

style={styles.inputfield}
/>
    

        </View>
        <View>
        <TouchableWithoutFeedback ><Text style={{color:'blue',paddingTop:20}}>Forgot Password?</Text></TouchableWithoutFeedback>
        </View>
        <View style={styles.buttoncontainer}>
            <TouchableOpacity  onPress={handleSignup} style={styles.button}><Text style={styles.buttonText}>Sign in</Text></TouchableOpacity>
        </View>
        <View>
           
            <TouchableWithoutFeedback  onPress={postData}>
            <Text style={{color:'blue',paddingTop:20}}>   sign up?</Text></TouchableWithoutFeedback>
        </View>
        <View>
            <Text>{help}</Text>
        </View>
        </View>
    )
}

export default Loginpage

const styles = StyleSheet.create({
    container:{
        flex: 1,
        justifyContent:'center',
        alignItems:'center'
    },
    mainimage:
        { width:240, 
            height: 240,
            justifyContent:'center',
            alignSelf:'center',
            marginBottom:50 }
        ,
        inputcontainer:{
            width:'80%'
        },
        inputfield:{
            backgroundColor:'white',
            paddingVertical:10,
            paddingHorizontal:15,
            borderRadius:10,
            marginTop:15,
           
        },
        buttoncontainer:{
            width:'80%',
            justifyContent:'center',
            alignItems:'center',
            marginTop:40
        },
        button:{
            backgroundColor:'#50C2C9',
            width:'100%',
            padding:20,
            alignItems:'center',
            

        }
        ,buttonText:
        {
            color:'white',
            marginTop:5,
            fontWeight:'700',
            fontSize:18,
        }
    
    
})
